<?php       include 'public.php';    $input = file_get_contents('php://input');    $arr = json_decode($input,true);      $pid=$arr['pid'];    $conn=consql();    if($conn){        $sql1="select ppicture from press where pid=$pid;";        $sql2="DELETE FROM press  WHERE pid = $pid;";                /*         * 删除图片         */        $res=mysqli_query($conn,$sql1);        $row=mysqli_fetch_array($res,MYSQLI_NUM);        if($row[0]!=""){            unlink ($row[0]);        }            /*         * 删除数据库数据         */        if(mysqli_query($conn,$sql2)==true){            echo 1;        }else{            echo 0;        }    }?>