<?php    include 'public.php';    function getUrlData($key){        $str=$_SERVER["QUERY_STRING"];        $arr= explode(';',$str);        foreach($arr as $a){            $k= explode('=',$a);            if(isset($k[1])&&$k[0]==$key){                return $k[1];            }        }        return "";    }    header("Content-Type: text/html;charset=utf-8");    $sql="select * from category";    $conn=consql();    $category=array(array("0","首页"));    $i=1;    if($conn){        $res=mysqli_query($conn,$sql);        while($row=mysqli_fetch_array($res,MYSQLI_NUM)){            $category[]=$row;        }    }    $kind=getUrlData('kind');    if(empty($kind)){        $kind=0;    }    $page=getUrlData('page');    $page=($page==""?0:$page);    foreach($category as $arr){        echo "<div >";        if($arr[0]==$kind){            echo "<a class=\"main_opbox_a\">$arr[1]</a>";        }else{            addHtml_a("http://localhost/NEWS/myNewsHome_html.php?kind=$arr[0];page=0",$arr[1],"main_opbox_a");        }        echo "</div >";    }     /*     * 动态添加CSS：     */        echo "<script language='javascript'>";    echo     "     var d=document.getElementById(\"main_opbox\");    var arr=d.getElementsByTagName(\"div\");";    for($i=0;$i<count($category);$i++){        echo         "         arr[$i].style.marginRight='10px';        arr[$i].style.width='120px';        arr[$i].style.height='50px';        arr[$i].style.float='left';        arr[$i].style.textAlign='center';//水平居中        arr[$i].style.borderRadius='25px';        if(".$category[$i][0]."==$kind){            arr[$i].style.backgroundColor=\"1d3cdd\";        }else{            arr[$i].onmouseover=function (){                this.style.backgroundColor=\"red\";                return true;            }            arr[$i].onmouseout=function (){                this.style.backgroundColor=\"\";                return true;            }        }        ";    }    echo "</script>";    ?>